sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    if (event.payload.value.length > 0)
        api.setState('collapseAll', false);
    if (event.payload.value.length == 0)
        api.setState('collapseAll', true);
}
