sys_ux_data_broker_scriptlet.script:
function transform() {
    var result = [];
    var obj = {};
    var grApps = new GlideRecord('sys_scope');
    grApps.query();
    while (grApps.next()) {
        obj.id = grApps.getValue('sys_id');
        obj.label = grApps.getValue('name');
        result.push(obj);
    }
    return result;

}
