sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {TransformApiHelpers} params.helpers
 */
function evaluateProperty({
    api,
    helpers
}) {
    return api.state.Result.filter(item => item.hits.length > 0 ? true : false).map((item) => {

        return {
            'id': item.recordType,
            'label': item.tableLabel,
            'children': item.hits.map((hit) => {
                return {
                    'id': hit.tableLabel,
                    'label': hit.name
                };
            })
        };

    });
}
