sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    var cusUpdates =  api.data.get_customer_updates_1.output.data.GlideRecord_Query.sys_update_xml._results.map((item) => {
        return {
            'id': item.name.value,
            'label': item.type.value
        };
    });
    console.log(cusUpdates)
    api.setState('customerUpdates',cusUpdates);
}
